class Markdown { constructor() { this.init(), this.addEventListeners() } init() { const e = this.input = CodeMirror.fromTextArea(document.querySelector(".js-input"), { lineNumbers: !0, matchBrackets: !1, mode: "text/x-markdown" }); this.compile(e.getValue()) } compile(e) { const t = marked(e); document.querySelector("#markdown-output").innerHTML = t } addEventListeners() { const { input: e } = this; let t; e.on("change", (() => { clearTimeout(t), t = setTimeout((() => this.compile(e.getValue())), 300) })) } }